package io.github.nosequel.tab.shared.skin;

import lombok.AllArgsConstructor;
import lombok.Getter;
import org.bukkit.Bukkit;

@AllArgsConstructor
@Getter
public class SkinType {

    public static SkinType DARK_GRAY = new SkinType(new String[]{"", ""});
    public static SkinType ONLINE = new SkinType(new String[]{"", ""});
    public static SkinType OFFLINE = new SkinType(new String[]{"", ""});
    public static SkinType DISCORD = new SkinType(new String[]{"", ""});
    public static SkinType TWITTER = new SkinType(new String[]{"", ""});
    public static SkinType LIGHT_GRAY = new SkinType(new String[]{"", ""});

    private final String[] skinData;

    public static SkinType fromUsername(String username) {
        return new SkinType(SkinUtil.getSkinData(Bukkit.getOfflinePlayer(username).getUniqueId()));
    }
}
